INSERT INTO Drug_Tables VALUES (NULL,NULL,NULL,'HALOGENATED HYDROCARBON ANESTHETICS','N01AB0-001',NULL,NULL,NULL,'BETA BLOCKING AGENTS (EXCEPT ESMOLOL)','C07AB-002','ANSM','43-HALOGENATED-HYDROCARBON-ANESTHETICS.html','Reduction of the compensatory cardiovascular reactions by the beta blocking agents. The beta-andrenergic inhibition can be elevated by the beta-mimetics during surgery. ','Precaution for use','As a general rule, do not stop the treatment with the beta-blocking agent and, in any event, avoid stopping it abruptly. Inform the anesthesiologist of this treatment. ');
